import React, {Component} from 'react';
import Table from "@material-ui/core/Table";
import Container from "@material-ui/core/Container";
import Button from "@material-ui/core/Button";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import {makeStyles} from '@material-ui/core/styles';
import Typography from "@material-ui/core/Typography";

class UserList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [{"id": 1, "name": "Johssssssn", "surname": "Doe", "age": 54, "email": "empty"},
                {"id": 2, "name": "Johnтн", "surname": "Depp", "age": 58, "email": "super@email.com"}]
        };
        this.remove = this.remove.bind(this);
    }

    /*componentDidMount() {
        fetch("/rest/persons")
            .then(response => response.json())
            .then(data => this.setState({users: data}));
    }*/

    async remove(id) {
        await fetch(`/rest/persons?id=${id}`, {
            method: 'DELETE',
        }).then(() => {
            let updatedUsers = [...this.state.users].filter(i => i.id !== id);
            this.setState({users: updatedUsers});
        });
    }

    render() {
        const {users} = this.state;

        const userList = users.map(user => {
            return <tr key={user.id}>
                <td align={'center'}>{user.id}</td>
                <td align={'center'}>{user.name}</td>
                <td align={'center'}>{user.surname}</td>
                <td align={'center'}>{user.age}</td>
                <td align={'center'}>{user.email}</td>

                <td>
                    <ButtonGroup>
                        <Button size="sm" variant="contained" color="primary" to={"/users/" + user.id}>Edit</Button>
                        <Button size="sm" variant="contained" color="secondary"
                                onClick={() => this.remove(user.id)}>Delete</Button>
                    </ButtonGroup>
                </td>
            </tr>
        });

        return (
            <div>

                <Container fixed>
                    <h3 align={'center'}>User List</h3>
                    <Typography component="div" style={{backgroundColor: 'rgba(89,227,246,0.14)'}}>


                        <Table className="mt-1">
                            <thead>
                            <tr style={{backgroundColor: 'rgba(89,141,246,0.41)'}}>
                                <th align={'center'} width="10%">ID</th>
                                <th align={'center'} width="20%">First Name</th>
                                <th align={'center'} width="20%">Last Name</th>
                                <th align={'center'} width="10%">Age</th>
                                <th align={'center'} width="30%">Email</th>

                                <th width="20%">Actions</th>

                            </tr>
                            </thead>
                            <tbody>
                            {userList}
                            </tbody>
                        </Table>

                    </Typography>


                </Container>
            </div>
        );
    }

}

export default UserList;
