import React from 'react';
import Button from '@material-ui/core/Button';
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator';

class UserForm extends React.Component {

    state = {
        email: '',
        age: '',
        name: '',
        surname: ''
    }

    handleChangeEmail = (event) => {
        const email = event.target.value;
        this.setState({ email });
    }
    handleChangeAge = (event) => {
        const age = event.target.value;
        this.setState({ age });
    }
    handleChangeName = (event) => {
        const name = event.target.value;
        this.setState({ name });
    }
    handleChangeSurname = (event) => {
        const surname = event.target.value;
        this.setState({ surname });
    }

    handleSubmit = () => {
        alert(this.state.surname + " " + this.state.name)
    }

    render() {
        const { email } = this.state;
        const { age } = this.state;
        const { name } = this.state;
        const { surname } = this.state;
        return (
            <ValidatorForm
                ref="form"
                onSubmit={this.handleSubmit}
                onError={errors => console.log(errors)}
            >

                <TextValidator
                    label="Name"
                    onChange={this.handleChangeName}
                    name="name"
                    value={name}
                    validators={['required', 'matchRegexp:^([A-Z][a-z]{2,15})$']}
                    errorMessages={['this field is required', 'name is not valid']}
                />
                <TextValidator
                    label="Surname"
                    onChange={this.handleChangeSurname}
                    name="surname"
                    value={surname}
                    validators={['required', 'matchRegexp:^([A-Z][a-z]{2,15})$']}
                    errorMessages={['this field is required', 'surname is not valid']}
                />
                <TextValidator
                    label="Email"
                    onChange={this.handleChangeEmail}
                    name="email"
                    value={email}
                    validators={['required', 'isEmail']}
                    errorMessages={['this field is required', 'email is not valid']}

                />
                <TextValidator
                    label="Age"
                    onChange={this.handleChangeAge}
                    name="age"
                    value={age}
                    validators={['required','minNumber:1', 'maxNumber:116','matchRegexp:^[1-9][0-9]?$']}
                    errorMessages={['this field is required', 'email is not valid', 'some thing']}
                />
                <Button type="submit" >Submit</Button>
            </ValidatorForm>
        );
    }
}
 export default UserForm;
