import React, {Component} from 'react';


import {validEmpty, validNameMaxLength, validName, validDate, validEmail} from "./validators/validator";
import {Button, Form, Label} from "reactstrap";


import {

    FieldFeedback,
    FieldFeedbacks,

    TextField
} from 'react-form-with-constraints-material-ui';


class UserForm extends Component {
    validationState = {
        firstName: {
            errors: [],
            validators: [validEmpty, validName, validNameMaxLength]
        },
        lastName: {
            errors: [],
            validators: [validEmpty, validName, validNameMaxLength],
        },
        email: {
            errors: [],
            validators: [validEmpty, validEmail],
        },
        birthDate: {
            errors: [],
            validators: [validEmpty, validDate],
        },
        allField: {
            errors: []
        }


    }


    emptyItem = {
        firstName: '',
        lastName: '',
        email: '',
        birthDate: ''
    };

    constructor(props) {
        super(props);
        this.state = {
            item: this.emptyItem,
            validation: this.validationState,
            isValidForm: false
        };
        this.updateInput = this.updateInput.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    updateInput(event) {
        this.setState({firstName: event.target.value})
    }

    handleSubmit() {
        alert('Your input value is: ' + this.state.firstName)
    }

    errorRender(validationFieldState) {
        if (validationFieldState.errors.length) {
            return validationFieldState.errors.map((error) => (<div className="error-field">- {error}</div>));
        }
        return <></>;
    }

    /*async componentDidMount() {
        if (this.props.match.params.id !== 'new') {
            /!*const user = await (await fetch(`/api/users?id=${this.props.match.params.id}`)).json();*!/
            this.initValidation(user);
        }
    }*/

    initValidation(user) {
        const validation = {...this.state.validation};
        for (let key in user) {
            if (key !== 'id') {
                validation[key] = this.validation(key, user[key].toString());
            }
        }

        const isValidForm = this.getValidationStatus(validation);
        this.setState({item: user, isValidForm, validation});
    }

    validation(fieldName, fieldValue) {
        const self = this;
        const fieldValidation = {...self.state.validation[fieldName], errors: []};
        fieldValidation.validators.forEach(validator => {
            const errors = fieldValidation.errors;
            fieldValidation.errors = [...errors, ...validator(fieldValue)];
        });

        return fieldValidation;
    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        let item = {...this.state.item};
        item[name] = value;
        const validation = {
            ...this.state.validation,
            [name]: this.validation(name, value.toString())
        };
        validation.allField.errors = [];
        const isValidForm = this.getValidationStatus(validation);
        this.setState({...this.state, validation, item, isValidForm});
    }

    getValidationStatus(validation) {
        return Object.values(validation)
            .every(validField => !validField.errors.length);
    }

    /*async handleSubmit(event) {
        const self = this;
        event.preventDefault();
        const {item} = this.state;
        const userData = {
            firstName: item.firstName,
            lastName: item.lastName,
            email: item.email,
            birthDate: item.birthDate
        }

        const endpoint = +item.id ? `/api/users?id=${item.id}` : '/api/users';

        await fetch(endpoint, {
            method: (item.id) ? 'PUT' : 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(userData),
        }).then(response => {
            if (![400, 500].includes(+response.status)) {
                window.location.href = 'http://34.88.120.86:3000/users';
                return null;
            }
            return response.text();
        }).then(message => {
            const validation = {...self.state.validation, allField: {errors: [message]}};
            self.setState({...self.state, validation});
        });
    }*/

    render() {

        return (
            <div>

                <TextField
                    type="password"
                    name="passwordConfirm"
                    label="Confirm Password"
                    value={this.inputs.passwordConfirm}
                    onChange={this.handleChange}
                    fullWidth
                    margin="dense"
                    helperText={
                        <FieldFeedbacks for="passwordConfirm">
                            <FieldFeedback when={value => value !== this.password.current.value}>
                                Not the same password
                            </FieldFeedback>
                        </FieldFeedbacks>
                    }
                />



            </div>

        )
    }
}

export default UserForm;
