import React, {Component} from 'react';
import UserForm from "./UserForm";
import UserList from "./UserList";

class MainPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [],
            user: {
                "id": '',
                "name": '',
                "surname": '',
                "age": '',
                "email": ''
            }
        };
        this.removeUser = this.removeUser.bind(this);
        this.addUser = this.addUser.bind(this);
        this.editUser = this.editUser.bind(this);
        this.saveUser = this.saveUser.bind(this);
    }

    componentDidMount() {
        /*fetch("http://localhost:8081/project/rest/persons").then(async response => {
            try {
                alert(response.status)
                const data = await response.json()
                console.log('response data?', data)
            } catch(error) {
                console.log('Error happened here!')
                console.error(error)
            }
        })*/
        fetch("http://localhost:8081/project/rest/persons")
            .then(response => {
                    alert(response.ok)
                response.json()
                    .then(data =>
                        /*data.forEach(user => this.users.push(user)),*/
                        this.setState(JSON.parse(data)
                        ));
                }
            )

    }

    removeUser(id) {
        let updatedUsers = [...this.state.users].filter(i => i.id !== id);
        this.setState({users: updatedUsers});
    }

    saveUser(user) {

        if (user.id === '') {
            this.addUser(user)
        } else {
            this.updateUser(user)
        }
    }

    updateUser(user) {
        const updateIndex = this.state.users.findIndex(item => item.id === user.id)
        let updatedUsers = [...this.state.users.slice(0, updateIndex), user, ...this.state.users.slice(updateIndex + 1)]
        this.setState({users: updatedUsers});
    }

    addUser(user) {
        let updatedUsers = [
            ...this.state.users,
            user
        ]
        this.setState({users: updatedUsers});
    }

    editUser(user) {
        this.state.user.id = user.id
        this.setState({user: user})
    }

    render() {
        document.body.style.backgroundColor = "rgba(167,165,74,0.35)";
        return (
            <div align={"center"}>
                <UserForm users={this.state.users} saveUser={this.saveUser} userAttr={this.state.user}/>
                <UserList users={this.state.users} removeUser={this.removeUser} editUser={this.editUser}/>
            </div>
        )
    }
}

export default MainPage;
