import React from 'react';
import Button from '@material-ui/core/Button';
import {ValidatorForm, TextValidator} from 'react-material-ui-form-validator';
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import makeStyles from "@material-ui/core/styles/makeStyles";



class UserForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            id: '',
            email: '',
            age: '',
            name: '',
            surname: '',
            show: true
        };
        this.showFormAddUser = this.showFormAddUser.bind(this);
    }

    showFormAddUser() {
        //alert(this.state.show)
        let opened = this.state.show
        this.setState({
            show: !opened,
        })
        this.props.showFormAddUser(!opened)
        //this.props.show.setValue(this.state.show)
    }

    componentDidUpdate(prevProps, prevState) {
        if (this.props.userAttr !== prevProps.userAttr) {
            this.setState({
                id: this.props.userAttr.id,
                name: this.props.userAttr.name,
                surname: this.props.userAttr.surname,
                age: this.props.userAttr.age,
                email: this.props.userAttr.email
            });
        }
        if (this.props.show !== prevProps.show) {
            this.setState({
                show: this.props.show
            });
        }
    }

    handleChangeEmail = (event) => {
        const email = event.target.value;
        this.setState({email});
    }
    handleChangeAge = (event) => {
        const age = event.target.value;
        this.setState({age});
    }
    handleChangeName = (event) => {
        const name = event.target.value;
        this.setState({name});
    }
    handleChangeSurname = (event) => {
        const surname = event.target.value;
        this.setState({surname});
    }

    handleSubmit = () => {
        const id = this.state.id
        this.setState({id});
        this.props.saveUser(this.state)
        this.clearForm()
    }

    clearForm() {
        this.setState({
            id: 0,
            name: '',
            surname: '',
            age: '',
            email: '',
            show: false
        });
    }

    render() {
        const useStyles = makeStyles((theme) => ({
            root: {
                display: 'flex',
            },
            details: {
                display: 'flex',
                flexDirection: 'column',
            },
            content: {
                flex: '1 0 auto',
            },
            cover: {
                width: 151,
            },
            controls: {
                display: 'flex',
                alignItems: 'center',
                paddingLeft: theme.spacing(1),
                paddingBottom: theme.spacing(1),
            },
            playIcon: {
                height: 38,
                width: 38,
            },
        }));
        return (
            <div>

                <Card className={classes.root}>
                    <div className={classes.details}>
                        <CardContent className={classes.content}>
                            <Typography component="h5" variant="h5">
                                Live From Space
                            </Typography>
                            <Typography variant="subtitle1" color="textSecondary">
                                Mac Miller
                            </Typography>
                        </CardContent>
                        <div className={classes.controls}>
                            <IconButton aria-label="previous">
                                {theme.direction === 'rtl' ? <SkipNextIcon /> : <SkipPreviousIcon />}
                            </IconButton>
                            <IconButton aria-label="play/pause">
                                <PlayArrowIcon className={classes.playIcon} />
                            </IconButton>
                            <IconButton aria-label="next">
                                {theme.direction === 'rtl' ? <SkipPreviousIcon /> : <SkipNextIcon />}
                            </IconButton>
                        </div>
                    </div>
                    <CardMedia
                        className={classes.cover}
                        image="/static/images/cards/live-from-space.jpg"
                        title="Live from space album cover"
                    />
                </Card>

                <div >
                    <AppBar position="static">
                        <Tabs  aria-label="simple tabs example">
                            <Tab label="Add User" onClick={this.showFormAddUser}/>

                        </Tabs>
                    </AppBar>

                </div>
                <div style={{visibility: this.state.show ? 'visible' : 'hidden' }}>
                    <ValidatorForm
                        ref="form"
                        onSubmit={this.handleSubmit}
                        onError={errors => console.log(errors)}
                    >
                        <br />
                        <TextValidator
                            label="Name"
                            onChange={this.handleChangeName}
                            name="name"
                            value={this.state.name}
                            validators={['required', 'matchRegexp:^([A-Z][a-z]{2,15})$']}
                            errorMessages={['this field is required', 'name is not valid']}
                        />
                        <TextValidator
                            label="Surname"
                            onChange={this.handleChangeSurname}
                            name="surname"
                            value={this.state.surname}
                            validators={['required', 'matchRegexp:^([A-Z][a-z]{2,15})$']}
                            errorMessages={['this field is required', 'surname is not valid']}
                        />
                        <TextValidator
                            label="Age"
                            onChange={this.handleChangeAge}
                            name="age"
                            value={this.state.age}
                            validators={['required', 'minNumber:1', 'maxNumber:116', 'matchRegexp:^[1-9][0-9]?$']}
                            errorMessages={['this field is required', 'email is not valid', 'some thing']}
                        />
                        <TextValidator
                            label="Email"
                            onChange={this.handleChangeEmail}
                            name="email"
                            value={this.state.email}
                            validators={['required', 'isEmail']}
                            errorMessages={['this field is required', 'email is not valid']}
                        />
                        <br />
                        <Button type="submit" variant="contained" color="primary">Submit</Button>
                    </ValidatorForm>
                </div>
            </div>
        );
    }
}

export default UserForm;
